mapper.update:
update member
set user_charicon = #{userCharIcon}
where user_id = #{userId}

mapper.select:
select count(user_id)
from member
where user_id = #{userId}

select count(user_email)
from member
where user_email = #{userEmail}

mapper.update:
update member
set user_pwd = #{userPwd}
where user_id = #{userId}
